import React from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import { useState, useEffect } from "react";
import nameLogo from "../assets/organizasso-name-transparent.png";
import { registerRoute } from "../utils/APIRoutes";

function RegisterPage() {
  const navigate = useNavigate();
  const toastOptions = {
    position: "bottom-right",
    autoClose: 3000,
    pauseOnHover: false,
    draggable: true,
    theme: "dark",
  };
  const [message, setMessage] = useState("");
  const [credentials, setCredentials] = useState({
    firstName: "",
    lastName: "",
    email: "",
    location: "",
    occupation: "",
    password: "",
  });
  const [confirmPassword, setConfirmPassword] = useState("");
  const handleChange = (event) => {
    setCredentials({
      ...credentials,
      [event.target.name]: event.target.value,
    });
  };

  // Checking if already authenticated
  /* useEffect(() => {
    if (localStorage.getItem("token")) {
      navigate("/");
    }
  }, []); */

  const handleValidation = () => {
    const { firstName, lastName, email, location, occupation, password } =
      credentials;
    const confirmPasswordd = confirmPassword;
    console.log(email);
    console.log(password);
    console.log(confirmPasswordd);
    if (password !== confirmPasswordd) {
      /* toast.error(
        "Password and confirm password should be same.",
        toastOptions
      ); */
      console.log("Password and confirm password should be same");
      setMessage("Password and confirm password should be same");
      return false;
    } else if (password.length < 8) {
      /* toast.error(
        "Password should be equal or greater than 8 characters.",
        toastOptions
      ); */
      console.log("Password should be equal or greater than 8 characters");
      setMessage("Password should be equal or greater than 8 characters");
      return false;
    } else if (email === "") {
      /* toast.error("Email is required.", toastOptions); */
      console.log("Email is required.");
      setMessage("Email is required");
      return false;
    }
    console.log("All checks pass");
    return true;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (handleValidation()) {
      try {
        const response = await axios.post(registerRoute, credentials);
        setMessage("User created successfully: " + response.data.username);

        const { token, user } = response.data;
        localStorage.setItem("token", token); // Store the tokens in localStorage o for later use

        if (response.data) {
          console.log(response.data);
          //login(response.data); // Pass user data to login function
        }
      } catch (error) {
        console.error("Register failed:", error);
      }
    }
  };

  return (
    <>
      <form action="" onSubmit={(event) => handleSubmit(event)}>
        <div className="brand">
          <img
            src={nameLogo}
            alt="Name Logo"
            style={{
              maxWidth: "200px",
            }}
          />
        </div>
        <input
          type="text"
          placeholder="First Name"
          name="firstname"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="text"
          placeholder="Last Name"
          name="lastname"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="email"
          placeholder="Email"
          name="email"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="text"
          placeholder="Location"
          name="location"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="text"
          placeholder="Occupation"
          name="occupation"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="password"
          placeholder="Password"
          name="password"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="password"
          placeholder="Confirm Password"
          name="confirmPassword"
          onChange={(event) => setConfirmPassword(event.target.value)}
        />
        <button type="submit">Register</button>
        <span>
          Already have an account? <Link to="/">Login!</Link>
        </span>
      </form>
      <p>{message}</p>
    </>
  );
}

export default RegisterPage;
