import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import { createPostRoute, getUsersPostsRoute } from "../utils/APIRoutes";
import "../styles/ProfilePage.css";

function Profile() {
  const [posts, setPosts] = useState([]);
  const [postContent, setPostContent] = useState("");
  const { user, userId, token } = useAuth();
  console.log(user);
  console.log(token);

  const handlePostSubmit = async (event) => {
    event.preventDefault();
    if (!postContent.trim()) return; // prevent empty posts
    try {
      const postData = {
        userId: user._id,
        content: postContent,
      };
      const response = await axios.post(createPostRoute, postData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setPosts([response.data, ...posts]); // prepend new post to the list
      setPostContent(""); // clear input field
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  const fetchUsersPosts = async () => {
    try {
      const response = await axios.get(
        `${getUsersPostsRoute}/${userId}/posts`,
        {
          headers: {
            Authorization: `Bearer ${token}`, // Include the token in the Authorization header
          },
        }
      );
      setPosts(response.data);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  useEffect(() => {
    fetchUsersPosts();
  }, [token]); // Depend on token to re-fetch if it changes

  return (
    <>
      <div className="profile-section">
        <h1>My Organiz'Asso Profile</h1>
        <p>
          <strong>Name:</strong> {user.firstName}
        </p>
        <p>
          <strong>Surname:</strong> {user.lastName}
        </p>
        <p>
          <strong>Email:</strong> {user.email}
        </p>
        <p>
          <strong>Location:</strong> {user.location}
        </p>
        <p>
          <strong>Occupation:</strong> {user.occupation}
        </p>
      </div>
      <div className="post-container">
        <form className="post-form" onSubmit={handlePostSubmit}>
          <textarea
            className="input-field"
            placeholder="What's on your mind?"
            value={postContent}
            onChange={(e) => setPostContent(e.target.value)}
          ></textarea>
          <button type="submit" className="submit-button">
            Post
          </button>
        </form>
        <h1>Your posts</h1>
        {posts.map((post) => (
          <div key={post._id} className="post-card">
            <div className="post-header">
              <span className="post-author">{`${post.firstName} ${post.lastName}`}</span>
              <span className="post-location">{post.location}</span>
            </div>
            <p className="post-content">{post.content}</p>
            <small className="post-date">
              {new Date(post.createdAt).toLocaleString()}
            </small>
          </div>
        ))}
      </div>
    </>
  );
}

export default Profile;
