import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/FriendsPage.css";
import { getUserRoute } from "../utils/APIRoutes";

function FriendsPage() {
  const { userId } = useParams();
  const [friends, setFriends] = useState([]);

  useEffect(() => {
    const fetchFriends = async () => {
      try {
        const response = await axios.get(`${getUserRoute}/${userId}/friends`);
        setFriends(response.data);
      } catch (error) {
        console.error("Failed to fetch friends:", error);
      }
    };

    fetchFriends();
  }, [userId]);

  return (
    <div>
      <h1>Friends</h1>
      <div>
        {friends.map((friend) => (
          <div key={friend.userId} className="friend-item">
            <p className="friend-name">{`${friend.firstName} ${friend.lastName}`}</p>
            <p className="friend-details">{friend.email}</p>
            <p className="friend-details">{friend.location}</p>
            <p className="friend-details">{friend.occupation}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default FriendsPage;
