import React, { useState, useEffect } from "react";
import axios from "axios";
import { getFeedPostsRoute, createPostRoute } from "../utils/APIRoutes";
import { useAuth } from "../context/AuthContext";
import Post from "../components/Post";
import NewPost from "../components/NewPost";

const FeedPage = () => {
  const [posts, setPosts] = useState([]);
  const { user, token } = useAuth();

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get(getFeedPostsRoute, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, [token]); // Fetch posts whenever the token changes

  const handlePostSubmit = async (event) => {
    event.preventDefault();
    if (!postContent.trim()) return; // prevent empty posts
    try {
      const postData = {
        userId: user._id, // Assume user._id is available from the auth context
        content: postContent,
      };
      const response = await axios.post(createPostRoute, postData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPosts([response.data, ...posts]); // Add the new post to the top of the list
      setPostContent(""); // Clear the input after successful post creation
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  const removePostFromState = (postId) => {
    setPosts((prevPosts) => prevPosts.filter((post) => post._id !== postId));
  };

  return (
    <div className="feed-container">
      <form className="post-form" onSubmit={handlePostSubmit}>
        <textarea
          className="input-field"
          placeholder="What's on your mind?"
          value={postContent}
          onChange={(e) => setPostContent(e.target.value)}
        ></textarea>
        <button type="submit" className="submit-button">
          Post
        </button>
      </form>
      {posts.map((post) => (
        <Post key={post._id} postData={post} removePost={removePostFromState} />
      ))}
    </div>
  );
};

export default FeedPage;
