import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import Post from "../components/Post";
import {
  createPostRoute,
  getUsersPostsRoute,
  getUserRoute,
} from "../utils/APIRoutes";
import "../styles/ProfilePage.css";

function Profile() {
  const { userId: profileUserId } = useParams();
  const [posts, setPosts] = useState([]);
  const [postContent, setPostContent] = useState("");
  const [profileUser, setProfileUser] = useState({});
  const { user, token } = useAuth();

  const handlePostSubmit = async (event) => {
    event.preventDefault();
    if (!postContent.trim()) return; // prevent empty posts
    try {
      const postData = {
        userId: profileUserId,
        content: postContent,
      };
      const response = await axios.post(createPostRoute, postData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setPosts([response.data, ...posts]); // prepend new post to the list
      setPostContent(""); // clear input field
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  const fetchProfile = async () => {
    try {
      const response = await axios.get(`${getUserRoute}/${profileUserId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setProfileUser(response.data);
    } catch (error) {
      console.error("Error fetching profile:", error);
    }
  };

  const fetchUsersPosts = async () => {
    try {
      const response = await axios.get(
        `${getUsersPostsRoute}/${profileUserId}/posts`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setPosts(response.data);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  useEffect(() => {
    fetchProfile();
    fetchUsersPosts();
  }, [profileUserId, token]); // Depend on profileUserId and token to re-fetch if they change

  return (
    <>
      <div className="profile-section">
        <h1>Organiz'Asso Profile</h1>
        <p>
          <strong>Name:</strong> {profileUser.firstName}
        </p>
        <p>
          <strong>Surname:</strong> {profileUser.lastName}
        </p>
        <p>
          <strong>Email:</strong> {profileUser.email}
        </p>
        <p>
          <strong>Location:</strong> {profileUser.location}
        </p>
        <p>
          <strong>Occupation:</strong> {profileUser.occupation}
        </p>
        {user._id === profileUserId && (
          <Link
            to={`/profile/${profileUserId}/friends`}
            className="friends-button"
          >
            Friends
          </Link>
        )}
        {user._id === profileUserId && (
          <form className="post-form" onSubmit={handlePostSubmit}>
            <textarea
              className="input-field"
              placeholder="What's on your mind?"
              value={postContent}
              onChange={(e) => setPostContent(e.target.value)}
            ></textarea>
            <button type="submit" className="submit-button">
              Post
            </button>
          </form>
        )}
        <h1>Posts</h1>
        {posts.map((post) => (
          <Post key={post._id} postData={post} />
        ))}
      </div>
    </>
  );
}

export default Profile;
