import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  getFeedPostsRoute,
  createPostRoute,
  getUserRoute,
  likePostRoute,
} from "../utils/APIRoutes";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "../styles/FeedPage.css";

const FeedPage = () => {
  const [posts, setPosts] = useState([]);
  const [postContent, setPostContent] = useState("");
  const { user, token } = useAuth();
  console.log(user);
  console.log(token);

  const handleAddRemoveFriend = async (friendId) => {
    try {
      const result = await axios.patch(
        `${getUserRoute}/${user._id}/${friendId}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const { friendsList, isAdded } = result.data;
      if (isAdded) {
        console.log("Friend added");
      } else {
        console.log("Friend removed");
      }
    } catch (error) {
      console.error("Error changing friendship status:", error);
    }
  };

  const handlePostSubmit = async (event) => {
    event.preventDefault();
    if (!postContent.trim()) return; // prevent empty posts
    try {
      const postData = {
        userId: user._id,
        content: postContent,
      };
      const response = await axios.post(createPostRoute, postData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setPosts([response.data, ...posts]); // prepend new post to the list
      setPostContent(""); // clear input field
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  const fetchPosts = async () => {
    try {
      const response = await axios.get(getFeedPostsRoute, {
        headers: {
          Authorization: `Bearer ${token}`, // Include the token in the Authorization header
        },
      });
      const postsWithFriendStatus = response.data.map((post) => ({
        ...post,
        isFriend: false, // Default to not friends; adjust based on your actual data setup
      }));
      setPosts(postsWithFriendStatus);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  const handleLikePost = async (postId) => {
    try {
      const response = await axios.patch(
        `${likePostRoute}/${postId}/like`,
        { userId: user._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const updatedPosts = posts.map((post) =>
        post._id === postId ? { ...post, likes: response.data.likes } : post
      );
      setPosts(updatedPosts);
    } catch (error) {
      console.error("Error liking post:", error);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, [token]); // Depend on token to re-fetch if it changes

  return (
    <div className="feed-container">
      <form className="post-form" onSubmit={handlePostSubmit}>
        <textarea
          className="input-field"
          placeholder="What's on your mind?"
          value={postContent}
          onChange={(e) => setPostContent(e.target.value)}
        ></textarea>
        <button type="submit" className="submit-button">
          Post
        </button>
      </form>
      {posts.map((post) => (
        <div key={post._id} className="post-card">
          <div className="post-header">
            <Link to={`/profile/${post.userId}`} className="post-author">
              {`${post.firstName} ${post.lastName}`}
            </Link>
            <button
              onClick={() => handleLikePost(post._id)}
              className="like-button"
            >
              Like ({Object.keys(post.likes || {}).length})
            </button>
            {user._id !== post.userId && (
              <button
                onClick={() => handleAddRemoveFriend(post.userId)}
                className="friend-button"
              >
                Add/Remove Friend
              </button>
            )}
          </div>

          <p className="post-content">{post.content}</p>
          <small className="post-location">{post.location}</small>
          <small className="post-date">
            {new Date(post.createdAt).toLocaleString()}
          </small>
        </div>
      ))}
    </div>
  );
};

export default FeedPage;
