import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { hostRoute } from "../utils/APIRoutes";
import "../styles/AdminPanelPage.css";

function AdminPanel() {
  const [users, setUsers] = useState([]);
  const { token } = useAuth();

  const fetchUsers = async () => {
    try {
      const result = await axios.get(`${hostRoute}/admin/unapproved-users`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(result.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const approveUser = async (id) => {
    try {
      const result = await axios.patch(
        `/admin/approve-user/${userId}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUsers(users.filter((user) => user._id !== userId));
      console.log("User approved:", result.data);
    } catch (error) {
      console.error("Error approving user:", error);
    }
  };

  const toggleAdmin = async (id, isAdmin) => {
    try {
      await axios.patch(
        `/admin/set-admin/${id}`,
        { isAdmin },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUsers(
        users.map((user) =>
          user._id === id ? { ...user, isAdmin: !user.isAdmin } : user
        )
      );
    } catch (error) {
      console.error("Failed to update admin status:", error);
    }
  };

  return (
    <div className="admin-panel">
      <h1>Admin Panel</h1>
      <p>Waiting for approval:</p>
      <ul>
        {users.map((user) => (
          <li key={user._id}>
            {user.firstName} {user.lastName}
            <button onClick={() => approveUser(user._id)}>Approve</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminPanel;
