import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  getFeedPostsRoute,
  createPostRoute,
  getUserRoute,
} from "../utils/APIRoutes";
import { useAuth } from "../context/AuthContext";
import "../styles/FeedPage.css";

const FeedPage = () => {
  const [posts, setPosts] = useState([]);
  const [postContent, setPostContent] = useState("");
  const { user, token } = useAuth();
  console.log(user);
  console.log(token);

  const generatePostKey = (post) => {
    return `${post.userId}-${post.firstName}-${
      post.lastName
    }-${post.createdAt.getTime()}`;
  };

  const handleAddRemoveFriend = async (friendId, postKey) => {
    try {
      const result = await axios.patch(
        `${getUserRoute}/${user._id}/${friendId}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const { friendsList, isAdded } = result.data;
      // Update the specific post's friendship status in the state
      setPosts((currentPosts) =>
        currentPosts.map((post) => {
          if (generatePostKey(post) === postKey) {
            return { ...post, isFriend: isAdded };
          }
          return post;
        })
      );
      console.log(isAdded ? "Friend added" : "Friend removed");
    } catch (error) {
      console.error("Error changing friendship status:", error);
    }
  };

  const handlePostSubmit = async (event) => {
    event.preventDefault();
    if (!postContent.trim()) return; // prevent empty posts
    try {
      const postData = {
        userId: user._id,
        content: postContent,
      };
      const response = await axios.post(createPostRoute, postData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setPosts([response.data, ...posts]); // prepend new post to the list
      setPostContent(""); // clear input field
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  const fetchPosts = async () => {
    try {
      const response = await axios.get(getFeedPostsRoute, {
        headers: {
          Authorization: `Bearer ${token}`, // Include the token in the Authorization header
        },
      });
      const postsWithFriendStatus = response.data.map((post) => ({
        ...post,
        isFriend: false, // Default to not friends; adjust based on your actual data setup
      }));
      setPosts(postsWithFriendStatus);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, [token]); // Depend on token to re-fetch if it changes

  return (
    <div className="feed-container">
      <form className="post-form" onSubmit={handlePostSubmit}>
        <textarea
          className="input-field"
          placeholder="What's on your mind?"
          value={postContent}
          onChange={(e) => setPostContent(e.target.value)}
        ></textarea>
        <button type="submit" className="submit-button">
          Post
        </button>
      </form>
      {posts.map((post) => (
        <div key={post._id} className="post-card">
          <div className="post-header">
            <span className="post-author">{`${post.firstName} ${post.lastName}`}</span>
            <span className="post-location">{post.location}</span>
            {user._id !== post.userId && (
              <button
                onClick={() => handleAddRemoveFriend(post.userId, postKey)}
                className="friend-button"
              >
                {post.isFriend ? "Remove" : "Add"} Friend
              </button>
            )}
          </div>

          <p className="post-content">{post.content}</p>
          <small className="post-date">
            {new Date(post.createdAt).toLocaleString()}
          </small>
        </div>
      ))}
    </div>
  );
};

export default FeedPage;
