import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/FriendsPage.css";
import { getUserRoute } from "../utils/APIRoutes";
import { useAuth } from "../context/AuthContext";

function FriendsPage() {
  const { userId } = useParams();
  const [friends, setFriends] = useState([]);
  const { user, token } = useAuth();

  useEffect(() => {
    const fetchFriends = async () => {
      try {
        const response = await axios.get(`${getUserRoute}/${userId}/friends`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setFriends(response.data);
      } catch (error) {
        console.error("Failed to fetch friends:", error);
      }
    };

    fetchFriends();
  }, [userId]);

  return (
    <div>
      <h1>Number of Friends: {friends.length}</h1>
      <div>
        {friends.length > 0 ? (
          friends.map((friend) => (
            <div key={friend.userId} style={{ marginBottom: "20px" }}>
              <p
                style={{ fontWeight: "bold" }}
              >{`${friend.firstName} ${friend.lastName}`}</p>

              <p style={{ fontSize: "smaller" }}>{friend.email}</p>
              <p style={{ fontSize: "smaller" }}>{friend.location}</p>
              <p style={{ fontSize: "smaller" }}>{friend.occupation}</p>
            </div>
          ))
        ) : (
          <p>No friends to display.</p>
        )}
      </div>
    </div>
  );
}

export default FriendsPage;
