import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import ProfilePage from "./pages/ProfilePage";
import HomePage from "./pages/HomePage";
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import Navbar from "./pages/Navbar";
import { AuthProvider, useAuth } from "./context/AuthContext";

export default function App() {
  return (
    <BrowserRouter>
      <AuthProvider>
        {" "}
        // Provide the AuthContext here
        <Navbar />
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route
            path="/login"
            element={
              <ProtectedRoute redirectTo="/profile" component={LoginPage} />
            }
          />
          <Route
            path="/register"
            element={
              <ProtectedRoute redirectTo="/profile" component={RegisterPage} />
            }
          />
          <Route
            path="/profile"
            element={<PrivateRoute component={ProfilePage} />}
          />
          <Route path="*" element={<Navigate replace to="/" />} />
        </Routes>
      </AuthProvider>
    </BrowserRouter>
  );
}

// Component to protect routes that require the user to be authenticated
function PrivateRoute({ component: Component }) {
  const { user } = useAuth();
  return user ? <Component /> : <Navigate to="/login" />;
}

// Component to redirect authenticated users away from login/register pages
function ProtectedRoute({ component: Component, redirectTo }) {
  const { user } = useAuth();
  return user ? <Navigate to={redirectTo} /> : <Component />;
}
