          },
        });
        setPosts(response.data); // Assuming the response data is the array of posts
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, [token]); // Depend on token to re-fetch if it changes*/

  return (
    <div>
      {posts.map((post) => (
        <div
          key={post._id}
          style={{
            marginBottom: "20px",
            border: "1px solid #ccc",
            padding: "10px",
          }}
        >
          <h4>{`${post.firstName} ${post.lastName} - ${post.location}`}</h4>
          <p>{post.content}</p>
          <small>{new Date(post.createdAt).toLocaleString()}</small>
        </div>
      ))}
    </div>
  );
};

export default FeedPage;
