import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { searchPostsRoute } from "../utils/APIRoutes";
import { useAuth } from "../context/AuthContext";
import "../styles/SearchResultsPage.css"; // Assume some basic CSS for styling

const SearchResultsPage = () => {
  const { search } = useLocation();
  const [posts, setPosts] = useState([]);
  const query = new URLSearchParams(search).get("query");
  const { user, token } = useAuth();

  useEffect(() => {
    const fetchSearchResults = async () => {
      try {
        const response = await axios.get(`${searchPostsRoute}?query=${query}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setPosts(response.data);
      } catch (error) {
        console.error("Failed to fetch search results:", error);
      }
    };

    if (query) {
      fetchSearchResults();
    }
  }, [query]);

  return (
    <div>
      <h1>Search Results for: {query}</h1>
      <div className="results-list">
        {posts.map((post) => (
          <div key={post._id} className="result-item">
            <h3>{post.content}</h3>
            <p>
              Author: {post.firstName} {post.lastName}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchResultsPage;
