import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import { createPostRoute } from "../utils/APIRoutes";

function Profile() {
  const [posts, setPosts] = useState([]);
  const [postContent, setPostContent] = useState("");
  const { user, token } = useAuth();
  console.log(user);
  console.log(token);

  const handlePostSubmit = async (event) => {
    event.preventDefault();
    if (!postContent.trim()) return; // prevent empty posts
    try {
      const postData = {
        userId: user._id,
        content: postContent,
      };
      const response = await axios.post(createPostRoute, postData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setPosts([response.data, ...posts]); // prepend new post to the list
      setPostContent(""); // clear input field
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  const fetchPosts = async () => {
    try {
      const response = await axios.get(getFeedPostsRoute, {
        headers: {
          Authorization: `Bearer ${token}`, // Include the token in the Authorization header
        },
      });
      setPosts(response.data);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, [token]); // Depend on token to re-fetch if it changes

  return (
    <>
      <div style={{ padding: "20px" }}>
        <h1>My Organiz'Asso Profile</h1>
        <p>
          <strong>Name:</strong> {user.firstName}
        </p>
        <p>
          <strong>Surname:</strong> {user.lastName}
        </p>
        <p>
          <strong>Email:</strong> {user.email}
        </p>
        <p>
          <strong>Location:</strong> {user.location}
        </p>
        <p>
          <strong>Occupation:</strong> {user.occupation}
        </p>
      </div>
    </>
  );
}

export default Profile;
