import React, { useState, useEffect } from "react";
import axios from "axios";
import { getFeedPostsRoute } from "../utils/APIRoutes";
import { useAuth } from "../context/AuthContext";
import "../styles/FeedPage.css";

const FeedPage = () => {
  const [posts, setPosts] = useState([]);
  const [postContent, setPostContent] = useState("");
  const { user, token } = useAuth();
  console.log(user);
  console.log(token);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(getFeedPostsRoute, {
        headers: {
          Authorization: `Bearer ${token}`, // Include the token in the Authorization header
        },
      });
      setPosts(response.data);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  const handlePostSubmit = async (event) => {
    event.preventDefault();
    if (!postContent.trim()) return; // prevent empty posts
    try {
      const postData = {
        userId: user._id, // assuming user object contains _id
        content: postContent,
      };
      const response = await axios.post(
        "http://localhost:5001/posts",
        postData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setPosts([response.data, ...posts]); // prepend new post to the list
      setPostContent(""); // clear input field
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, [token]); // Depend on token to re-fetch if it changes

  return (
    <div className="feed-container">
      {posts.map((post) => (
        <div key={post._id} className="post-card">
          <div className="post-header">
            <span className="post-author">{`${post.firstName} ${post.lastName}`}</span>
            <span className="post-location">{post.location}</span>
          </div>
          <p className="post-content">{post.content}</p>
          <small className="post-date">
            {new Date(post.createdAt).toLocaleString()}
          </small>
        </div>
      ))}
    </div>
  );
};

export default FeedPage;
