import React from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import { useState, useEffect } from "react";
import nameLogo from "../assets/organizasso-name-transparent.png";
import { registerRoute } from "../utils/APIRoutes";

function RegisterPage() {
  const navigate = useNavigate();
  const toastOptions = {
    position: "bottom-right",
    autoClose: 8000,
    pauseOnHover: true,
    draggable: true,
    theme: "dark",
  };

  const [credentials, setCredentials] = useState({
    firstName: "",
    lastName: "",
    email: "",
    location: "",
    occupation: "",
    password: "",
    confirmPassword: "",
  });

  const handleChange = (event) => {
    setCredentials({
      ...credentials,
      [event.target.name]: event.target.value,
    });
  };

  // Checking if already authenticated
  /* useEffect(() => {
    if (localStorage.getItem("token")) {
      navigate("/");
    }
  }, []); */

  const handleValidation = () => {
    const {
      firstName,
      lastName,
      email,
      location,
      occupation,
      password,
      confirmPassword,
    } = credentials;
    if (password !== confirmPassword) {
      toast.error(
        "Password and confirm password should be same.",
        toastOptions
      );
    } else if (password.length < 8) {
      toast.error("Password should be equal or greater than 8 characters.");
      console.log("Password should be equal or greater than 8 characters.");
      return false;
    } else if (email === "") {
      toast.error("Email is required.");
      console.log("Email is required.");
      return false;
    }
    return true;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (handleValidation()) {
      try {
        const response = await axios.post(registerRoute, credentials);
        const { token, user } = response.data;

        localStorage.setItem("token", token); // Store the tokens in localStorage o for later use

        if (response.data) {
          console.log(response.data);
          //login(response.data); // Pass user data to login function
        }
      } catch (error) {
        console.error("Register failed:", error);
      }
    }
  };

  return (
    <form action="" onSubmit={(event) => handleSubmit(event)}>
      <div className="brand">
        <img
          src={nameLogo}
          alt="Name Logo"
          style={{
            maxWidth: "200px",
          }}
        />
      </div>
      <input
        type="text"
        placeholder="First Name"
        name="firstname"
        onChange={(event) => handleChange(event)}
      />
      <input
        type="text"
        placeholder="Last Name"
        name="lastname"
        onChange={(event) => handleChange(event)}
      />
      <input
        type="email"
        placeholder="Email"
        name="email"
        onChange={(event) => handleChange(event)}
      />
      <input
        type="text"
        placeholder="Location"
        name="location"
        onChange={(event) => handleChange(event)}
      />
      <input
        type="text"
        placeholder="Occupation"
        name="occupation"
        onChange={(event) => handleChange(event)}
      />
      <input
        type="password"
        placeholder="Password"
        name="password"
        onChange={(event) => handleChange(event)}
      />
      <input
        type="password"
        placeholder="Confirm Password"
        name="confirmPassword"
        onChange={(event) => handleChange(event)}
      />
      <button type="submit">Register</button>
      <span>
        Already have an account? <Link to="/">Login!</Link>
      </span>
    </form>
  );
}

export default RegisterPage;
