import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "../state/index";
import { loginRoute } from "../utils/APIRoutes";
import { ToastContainer, toast } from "react-toastify";

function LoginPage() {
  const navigate = useNavigate();
  const toastOptions = {
    position: "bottom-right",
    autoClose: 8000,
    pauseOnHover: true,
    draggable: true,
    theme: "dark",
  };

  const [user, setUser] = useState(null);

  const [credentials, setCredentials] = useState({
    email: "",
    password: "",
  });

  const handleChange = (event) => {
    setCredentials({
      ...credentials,
      [event.target.name]: event.target.value,
    });
  };

  // Checking if already authenticated
  useEffect(() => {
    if (localStorage.getItem("token")) {
      navigate("/");
    }
  }, []);

  const validateForm = () => {
    const { email, password } = credentials;
    if (email === "") {
      toast.error("Email and Password is required.", toastOptions);
      return false;
    } else if (password === "") {
      toast.error("Email and Password is required.", toastOptions);
      return false;
    }
    return true;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post(loginRoute, credentials);
      const { token, user } = response.data;
      setUser(user);
      localStorage.setItem("token", token); // Store the tokens in localStorage o for later use

      if (response.data) {
        console.log(response.data);
        //login(response.data); // Pass user data to login function
      }
    } catch (error) {
      console.error("Login failed:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        name="email"
        value={credentials.email}
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        value={credentials.password}
        onChange={handleChange}
      />
      <button type="submit">Login</button>
      <span>
        Don't have an account? <Link to="/register">Join us!</Link>
      </span>
    </form>
  );
}

export const currentUser = user;
export default LoginPage;
