import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import nameLogo from "../assets/organizasso-name-transparent.png";
import { useAuth } from "../context/AuthContext";
import { ToastContainer, toast } from "react-toastify";

function RegisterPage() {
  const navigate = useNavigate();
  const toastOptions = {
    position: "bottom-right",
    autoClose: 3000,
    pauseOnHover: false,
    draggable: true,
    theme: "dark",
  };
  const { register, user } = useAuth();
  const [message, setMessage] = useState("");
  const [credentials, setCredentials] = useState({
    firstName: "",
    lastName: "",
    email: "",
    location: "",
    occupation: "",
    password: "",
  });
  const [confirmPassword, setConfirmPassword] = useState("");

  // Checking if already authenticated
  useEffect(() => {
    if (user) {
      navigate("/"); // Navigate to the home page if already logged in
    }
  }, [user, navigate]);

  const handleChange = (event) => {
    setCredentials({
      ...credentials,
      [event.target.name]: event.target.value,
    });
  };

  const handleValidation = () => {
    const { firstName, lastName, email, location, occupation, password } =
      credentials;
    if (password !== confirmPassword) {
      setMessage("Password and confirm password should be the same");
      return false;
    } else if (password.length < 8) {
      setMessage("Password should be equal or greater than 8 characters");
      return false;
    } else if (email === "") {
      setMessage("Email is required");
      return false;
    }
    return true;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (handleValidation()) {
      try {
        await register(credentials);
        navigate("/"); // Navigate to the home page after successful registration
      } catch (error) {
        console.error("Registration failed:", error);
        setMessage("Registration failed. Please try again.");
      }
    }
  };

  return (
    <>
      <form action="" onSubmit={(event) => handleSubmit(event)}>
        <div className="brand">
          <img
            src={nameLogo}
            alt="Name Logo"
            style={{
              maxWidth: "200px",
            }}
          />
        </div>
        <input
          type="text"
          placeholder="First Name"
          name="firstName"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="text"
          placeholder="Last Name"
          name="lastName"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="email"
          placeholder="Email"
          name="email"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="text"
          placeholder="Location"
          name="location"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="text"
          placeholder="Occupation"
          name="occupation"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="password"
          placeholder="Password"
          name="password"
          onChange={(event) => handleChange(event)}
        />
        <input
          type="password"
          placeholder="Confirm Password"
          name="confirmPassword"
          onChange={(event) => setConfirmPassword(event.target.value)}
        />
        <button type="submit">Register</button>
        <span>
          Already have an account? <Link to="/login">Login!</Link>
        </span>
      </form>
      <p>{message}</p>
    </>
  );
}

export default RegisterPage;
