import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { hostRoute } from "../utils/APIRoutes";
import "../styles/AdminPanelPage.css";

function AdminPanel() {
  const [unapprovedUsers, setUnapprovedUser] = useState([]);
  const [users, setUsers] = useState([]);
  const { userId, token } = useAuth();

  const fetchUnapprovedUsers = async () => {
    try {
      const result = await axios.get(`${hostRoute}/admin/unapproved-users`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUnapprovedUser(result.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  useEffect(() => {
    fetchUnapprovedUsers();
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const result = await axios.get(`${hostRoute}/user/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(result.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const approveUser = async (id) => {
    try {
      const result = await axios.patch(
        `/admin/approve-user/${id}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUnapprovedUser(users.filter((user) => user._id !== id));
      console.log("User approved:", result.data);
    } catch (error) {
      console.error("Error approving user:", error);
    }
  };

  const toggleAdmin = async (id) => {
    try {
      await axios.patch(
        `/admin/set-admin/${id}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUsers(
        users.map((user) =>
          user._id === id ? { ...user, isAdmin: !user.isAdmin } : user
        )
      );
    } catch (error) {
      console.error("Failed to update admin status:", error);
    }
  };

  return (
    <div className="admin-panel">
      <h1>Admin Panel</h1>
      <div>
        <p>Waiting for approval:</p>
        <ul>
          {unapprovedUsers.map((user) => (
            <li key={user._id}>
              {user.firstName} {user.lastName}
              <button onClick={() => approveUser(user._id)}>Approve</button>
            </li>
          ))}
        </ul>
      </div>
      <div>
        <p>Admins:</p>
        <ul>
          {users
            .filter((user) => user.isAdmin)
            .map((user) => (
              <li key={user._id}>
                {user.firstName} {user.lastName}
                {user._id != userId && (
                  <button onClick={() => toggleAdmin(user._id, false)}>
                    Remove Admin
                  </button>
                )}
              </li>
            ))}
        </ul>
      </div>
      <div>
        <p>Users:</p>
        <ul>
          {users.map((user) => (
            <li key={user._id}>
              {user.firstName} {user.lastName}
              {user.isAdmin
                ? " (Admin)"
                : user._id !== userId && (
                    <button
                      onClick={() => toggleAdmin(user._id, !user.isAdmin)}
                    >
                      Admin
                    </button>
                  )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default AdminPanel;
